```java
class Order {
    private String priority;

    public String getPriority() {
        return priority;
    }

    public void setPriority(String priority) {
        this.priority = priority;
    }

    public boolean isHighPriority() {
        return "high".equals(priority);
    }

    public boolean isRushPriority() {
        return "rush".equals(priority);
    }
}

class OrderComparator implements Comparator<Order> {

    @Override
    public int compare(Order o1, Order o2) {
        if (o1.isHighPriority() && !o2.isHighPriority()) {
            return -1;
        } else if (!o1.isHighPriority() && o2.isHighPriority()) {
            return 1;
        } else {
            return 0;
        }
    }

    public String nameLiar(Order o) {
        if (o.isRushPriority()) {
            return "Rush Order";
        } else {
            return "High Priority Order";
        }
    }
}

List<Order> filteredOrders = orders.stream()
    .filter(o -> o.isHighPriority() || o.isRushPriority())
    .sorted(new OrderComparator())
    .collect(Collectors.toList());

for (Order order : filteredOrders) {
    String liar = new OrderComparator().nameLiar(order);
    System.out.println(liar + " is a liar!");
}
```

