public double getTotalOutstandingAndSendBill() {
    double result = 0;
    // Calculate the total outstanding amount by summing up all invoices
    for (Invoice each : customer.getInvoices()) {
        result += each.getAmount();
    }
    // Send the bill to the customer after calculating the total outstanding amount
    sendBill();
    return result;
}
