     * Adds a course to the person's list of courses.
     *
     * @param courseName The name of the course to add.
     */
    public void addCourse(String courseName) {
        if (!courses.contains(courseName)) {
            courses.add(courseName);
        } else {
            System.out.println("Course already exists: " + courseName);
        }
    }

    /**
     * Removes a course from the person's list of courses.
     *
     * @param courseName The name of the course to remove.
     */
    public void removeCourse(String courseName) {
        if (courses.contains(courseName)) {
            courses.remove(courseName);
        } else {
            System.out.println("Course not found: " + courseName);
        }
    }

    @Override
    public String toString() {
        return "Person{" +
                "courses=" + courses +
                '}';
    }

